job.command:
starver &STAR_VERSION;

sed -e s/\$env\(STISCAN_Z\)/&STISCAN_Z;/g offline/hft/tests/stiscan_zslice.kumac > ./stiscan_zslice.kumac

echo
echo "OUT_DIR:        " &OUT_DIR;
echo "CODE_DIR:       " &CODE_DIR;
echo "JOB_RUN_FILE_NAME:   " &JOB_RUN_FILE_NAME;
echo "STAR_VERSION:   " &STAR_VERSION;
echo "STAR_HOST_SYS:  " &STAR_HOST_SYS;
echo "LOG_DIR:        " &LOG_DIR;
echo
echo "STAR:           " $STAR
echo "SCRATCH:        " $SCRATCH
echo "pwd:            " `pwd`
echo

ls -al .

starsim -w 0 -b ./stiscan_zslice.kumac
mv stiscan_zslice.kumac &JOB_RUN_FILE_NAME;.kumac
mv stiscan_zslice.fz &JOB_RUN_FILE_NAME;.fz

ls -al .

echo "End of job: tralala"
